INSERT INTO `dataset_pairs` VALUES (1,'add_item','describe(\'Todo List Management\', function () {\r\n  const TODO_ITEM_ONE = \'buy some cheese\';\r\n\r\n  beforeEach(function () {\r\n    cy.visit(\'http://localhost:8888/\');\r\n  });\r\n\r\n  afterEach(() => {\r\n    cy.window().then((win) => {\r\n      win.document.activeElement.blur();\r\n    });\r\n  });\r\n\r\n  it(\'Add a new todo item\', function () {\r\n    // Action: Create the todo\r\n    cy.get(\'.new-todo\').type(`${TODO_ITEM_ONE}{enter}`);\r\n\r\n    // Assertions\r\n    cy.get(\'.todo-list li\')\r\n      .should(\'have.length\', 1)\r\n      .and(\'contain.text\', TODO_ITEM_ONE);\r\n    \r\n    // Assert that the input field is cleared\r\n    cy.get(\'.new-todo\').should(\'have.text\', \'\');\r\n  });\r\n});','Feature: Task Management\r\n\r\n  Background:\r\n    Given I have an empty task list\r\n\r\n  @todo @core\r\n  Scenario: Capturing a new task\r\n    When I add the task \"buy some cheese\"\r\n    Then \"buy some cheese\" should be listed in my active tasks\r\n    And the total task count should be 1','TodoMVC','Low','https://github.com/cypress-io/cypress-example-todomvc'),(2,'auth','import { User } from \"../../../src/models\";\r\nimport { isMobile } from \"../../support/utils\";\r\n\r\nconst apiGraphQL = `${Cypress.env(\"apiUrl\")}/graphql`;\r\n\r\ndescribe(\"User Authentication and Onboarding\", function () {\r\n  beforeEach(function () {\r\n    cy.task(\"db:seed\");\r\n\r\n    cy.intercept(\"POST\", \"/users\").as(\"signup\");\r\n    cy.intercept(\"POST\", apiGraphQL, (req) => {\r\n      const { body } = req;\r\n\r\n      if (body.hasOwnProperty(\"operationName\") && body.operationName === \"CreateBankAccount\") {\r\n        req.alias = \"gqlCreateBankAccountMutation\";\r\n      }\r\n    });\r\n  });\r\n\r\n  it(\"Redirecting unauthenticated users\", function () {\r\n    cy.visit(\"/personal\");\r\n    cy.location(\"pathname\").should(\"equal\", \"/signin\");\r\n    cy.visualSnapshot(\"Redirect to SignIn\");\r\n  });\r\n\r\n  it(\"Successful login redirection\", function () {\r\n    cy.database(\"find\", \"users\").then((user: User) => {\r\n      cy.login(user.username, \"s3cret\", { rememberUser: true });\r\n    });\r\n    cy.location(\"pathname\").should(\"equal\", \"/\");\r\n  });\r\n\r\n  it(\"Persistent session and logout\", function () {\r\n    cy.database(\"find\", \"users\").then((user: User) => {\r\n      cy.login(user.username, \"s3cret\", { rememberUser: true });\r\n    });\r\n\r\n    // Verify Session Cookie\r\n    cy.getCookie(\"connect.sid\").should(\"have.property\", \"expiry\");\r\n\r\n    // Logout User\r\n    if (isMobile()) {\r\n      cy.getBySel(\"sidenav-toggle\").click();\r\n    }\r\n    cy.getBySel(\"sidenav-signout\").click();\r\n    cy.location(\"pathname\").should(\"eq\", \"/signin\");\r\n    cy.visualSnapshot(\"Redirect to SignIn\");\r\n  });\r\n\r\n  it(\"Visitor sign-up, login, onboarding, and logout flow\", function () {\r\n    const userInfo = {\r\n      firstName: \"Bob\",\r\n      lastName: \"Ross\",\r\n      username: \"PainterJoy90\",\r\n      password: \"s3cret\",\r\n    };\r\n\r\n    // Sign-up User\r\n    cy.visit(\"/\");\r\n\r\n    cy.getBySel(\"signup\").click();\r\n    cy.getBySel(\"signup-title\").should(\"be.visible\").and(\"contain\", \"Sign Up\");\r\n    cy.visualSnapshot(\"Sign Up Title\");\r\n\r\n    cy.getBySel(\"signup-first-name\").type(userInfo.firstName);\r\n    cy.getBySel(\"signup-last-name\").type(userInfo.lastName);\r\n    cy.getBySel(\"signup-username\").type(userInfo.username);\r\n    cy.getBySel(\"signup-password\").type(userInfo.password);\r\n    cy.getBySel(\"signup-confirmPassword\").type(userInfo.password);\r\n    cy.visualSnapshot(\"About to Sign Up\");\r\n    cy.getBySel(\"signup-submit\").click();\r\n    cy.wait(\"@signup\");\r\n\r\n    // Login User\r\n    cy.login(userInfo.username, userInfo.password);\r\n\r\n    // Onboarding\r\n    cy.getBySel(\"user-onboarding-dialog\").should(\"be.visible\");\r\n    cy.getBySel(\"list-skeleton\").should(\"not.exist\");\r\n    cy.getBySel(\"nav-top-notifications-count\").should(\"exist\");\r\n    cy.visualSnapshot(\"User Onboarding Dialog\");\r\n    cy.getBySel(\"user-onboarding-next\").click();\r\n\r\n    cy.getBySel(\"user-onboarding-dialog-title\").should(\"contain\", \"Create Bank Account\");\r\n\r\n    cy.getBySelLike(\"bankName-input\").type(\"The Best Bank\");\r\n    cy.getBySelLike(\"accountNumber-input\").type(\"123456789\");\r\n    cy.getBySelLike(\"routingNumber-input\").type(\"987654321\");\r\n    cy.visualSnapshot(\"About to complete User Onboarding\");\r\n    cy.getBySelLike(\"submit\").click();\r\n\r\n    cy.wait(\"@gqlCreateBankAccountMutation\");\r\n\r\n    cy.getBySel(\"user-onboarding-dialog-title\").should(\"contain\", \"Finished\");\r\n    cy.getBySel(\"user-onboarding-dialog-content\").should(\"contain\", \"You\'re all set!\");\r\n    cy.visualSnapshot(\"Finished User Onboarding\");\r\n    cy.getBySel(\"user-onboarding-next\").click();\r\n\r\n    cy.getBySel(\"transaction-list\").should(\"be.visible\");\r\n    cy.visualSnapshot(\"Transaction List is visible after User Onboarding\");\r\n\r\n    // Logout User\r\n    if (isMobile()) {\r\n      cy.getBySel(\"sidenav-toggle\").click();\r\n    }\r\n    cy.getBySel(\"sidenav-signout\").click();\r\n    cy.location(\"pathname\").should(\"eq\", \"/signin\");\r\n    cy.visualSnapshot(\"Redirect to SignIn\");\r\n  });\r\n\r\n  it(\"Displaying login form validation errors\", function () {\r\n    cy.visit(\"/\");\r\n\r\n    cy.getBySel(\"signin-username\").type(\"User\");\r\n    cy.getBySel(\"signin-username\").find(\"input\").clear();\r\n    cy.getBySel(\"signin-username\").find(\"input\").blur();\r\n    cy.get(\"#username-helper-text\").should(\"be.visible\").and(\"contain\", \"Username is required\");\r\n    cy.visualSnapshot(\"Display Username is Required Error\");\r\n\r\n    cy.getBySel(\"signin-password\").type(\"abc\");\r\n    cy.getBySel(\"signin-password\").find(\"input\").blur();\r\n    cy.get(\"#password-helper-text\")\r\n      .should(\"be.visible\")\r\n      .and(\"contain\", \"Password must contain at least 4 characters\");\r\n    cy.visualSnapshot(\"Display Password Error\");\r\n\r\n    cy.getBySel(\"signin-submit\").should(\"be.disabled\");\r\n    cy.visualSnapshot(\"Sign In Submit Disabled\");\r\n  });\r\n\r\n  it(\"Displaying sign-up form validation errors\", function () {\r\n    cy.intercept(\"GET\", \"/signup\");\r\n\r\n    cy.visit(\"/signup\");\r\n\r\n    cy.getBySel(\"signup-first-name\").type(\"First\");\r\n    cy.getBySel(\"signup-first-name\").find(\"input\").clear();\r\n    cy.getBySel(\"signup-first-name\").find(\"input\").blur();\r\n    cy.get(\"#firstName-helper-text\").should(\"be.visible\").and(\"contain\", \"First Name is required\");\r\n\r\n    cy.getBySel(\"signup-last-name\").type(\"Last\");\r\n    cy.getBySel(\"signup-last-name\").find(\"input\").clear();\r\n    cy.getBySel(\"signup-last-name\").find(\"input\").blur();\r\n    cy.get(\"#lastName-helper-text\").should(\"be.visible\").and(\"contain\", \"Last Name is required\");\r\n\r\n    cy.getBySel(\"signup-username\").type(\"User\");\r\n    cy.getBySel(\"signup-username\").find(\"input\").clear();\r\n    cy.getBySel(\"signup-username\").find(\"input\").blur();\r\n    cy.get(\"#username-helper-text\").should(\"be.visible\").and(\"contain\", \"Username is required\");\r\n\r\n    cy.getBySel(\"signup-password\").type(\"password\");\r\n    cy.getBySel(\"signup-password\").find(\"input\").clear();\r\n    cy.getBySel(\"signup-password\").find(\"input\").blur();\r\n    cy.get(\"#password-helper-text\").should(\"be.visible\").and(\"contain\", \"Enter your password\");\r\n\r\n    cy.getBySel(\"signup-confirmPassword\").type(\"DIFFERENT PASSWORD\");\r\n    cy.getBySel(\"signup-confirmPassword\").find(\"input\").blur();\r\n    cy.get(\"#confirmPassword-helper-text\")\r\n      .should(\"be.visible\")\r\n      .and(\"contain\", \"Password does not match\");\r\n    cy.visualSnapshot(\"Display Sign Up Required Errors\");\r\n\r\n    cy.getBySel(\"signup-submit\").should(\"be.disabled\");\r\n    cy.visualSnapshot(\"Sign Up Submit Disabled\");\r\n  });\r\n\r\n  it(\"Error handling for non-existent user login\", function () {\r\n    cy.login(\"invalidUserName\", \"invalidPa$$word\");\r\n\r\n    cy.getBySel(\"signin-error\")\r\n      .should(\"be.visible\")\r\n      .and(\"have.text\", \"Username or password is invalid\");\r\n    cy.visualSnapshot(\"Sign In, Invalid Username and Password, Username or Password is Invalid\");\r\n  });\r\n\r\n  it(\"Error handling for invalid password\", function () {\r\n    cy.database(\"find\", \"users\").then((user: User) => {\r\n      cy.login(user.username, \"INVALID\");\r\n    });\r\n\r\n    cy.getBySel(\"signin-error\")\r\n      .should(\"be.visible\")\r\n      .and(\"have.text\", \"Username or password is invalid\");\r\n    cy.visualSnapshot(\"Sign In, Invalid Username, Username or Password is Invalid\");\r\n  });\r\n});\r\n','Feature: User Authentication and Onboarding\r\n\r\n  Background:\r\n    Given \"Bob\" is a registered user\r\n\r\n  @auth @security\r\n  Scenario: Restricting access to protected areas\r\n    Given I am a visitor\r\n    When I attempt to access the \"Profile\" page\r\n    Then I should be required to log in\r\n\r\n  @auth @login\r\n  Scenario: Successful login\r\n    Given I am on the login page\r\n    When I log in with Bob\'s valid credentials\r\n    Then I should be authenticated\r\n    And I should be redirected to the Dashboard\r\n\r\n  @auth @session\r\n  Scenario: Persistent authentication (\"Remember Me\")\r\n    Given I am on the login page\r\n    When I log in with \"Remember Me\" enabled\r\n    Then I should remain authenticated after restarting the browser\r\n\r\n  @auth @logout\r\n  Scenario: User logout\r\n    Given I am currently logged in as \"Bob\"\r\n    When I log out\r\n    Then I should be returned to the public area\r\n\r\n  @auth @signup @onboarding\r\n  Scenario: New user registration and onboarding\r\n    Given I am a new visitor\r\n    When I register a new account with valid details\r\n    Then I should be immediately prompted to set up my bank account\r\n\r\n  @auth @onboarding\r\n  Scenario: Completing the onboarding wizard\r\n    Given I have just registered a new account\r\n    When I complete the bank account setup\r\n    Then I should see my empty Transaction List\r\n\r\n  @auth @validation\r\n  Scenario Outline: Registration form validation\r\n    Given I am on the Sign Up page\r\n    When I attempt to register with <condition>\r\n    Then the system should prevent registration\r\n    And show the error message \"<error>\"\r\n\r\n    Examples:\r\n      | condition          | error                                       |\r\n      | missing username   | Username is required                        |\r\n      | short password     | Password must contain at least 4 characters |\r\n      | password mismatch  | Passwords do not match                      |\r\n\r\n  @auth @error\r\n  Scenario Outline: Login failure handling\r\n    Given I am on the login page\r\n    When I attempt to log in with <credentials>\r\n    Then I should see the alert \"Username or password is invalid\"\r\n\r\n    Examples:\r\n      | credentials             |\r\n      | a non-existent username |\r\n      | an invalid password     |','Cypress Real World App','High','https://github.com/cypress-io/cypress-realworld-app'),(3,'bankaccounts','import { User } from \"../../../src/models\";\r\nimport { isMobile } from \"../../support/utils\";\r\n\r\nconst apiGraphQL = `${Cypress.env(\"apiUrl\")}/graphql`;\r\n\r\ntype BankAccountsTestCtx = {\r\n  user?: User;\r\n};\r\n\r\ndescribe(\"Bank Accounts\", function () {\r\n  const ctx: BankAccountsTestCtx = {};\r\n\r\n  beforeEach(function () {\r\n    cy.task(\"db:seed\");\r\n\r\n    cy.intercept(\"GET\", \"/notifications\").as(\"getNotifications\");\r\n\r\n    cy.intercept(\"POST\", apiGraphQL, (req) => {\r\n      const operationAliases: Record<string, string> = {\r\n        ListBankAccount: \"gqlListBankAccountQuery\",\r\n        CreateBankAccount: \"gqlCreateBankAccountMutation\",\r\n        DeleteBankAccount: \"gqlDeleteBankAccountMutation\",\r\n      };\r\n\r\n      const { body } = req;\r\n\r\n      const operationName = body?.operationName;\r\n\r\n      if (\r\n        body.hasOwnProperty(\"operationName\") &&\r\n        operationName &&\r\n        operationAliases[operationName]\r\n      ) {\r\n        req.alias = operationAliases[operationName];\r\n      }\r\n    });\r\n\r\n    cy.database(\"find\", \"users\").then((user: User) => {\r\n      ctx.user = user;\r\n\r\n      return cy.loginByXstate(ctx.user.username);\r\n    });\r\n  });\r\n\r\n  it(\"Creating a new bank account successfully\", function () {\r\n    cy.wait(\"@getNotifications\");\r\n    if (isMobile()) {\r\n      cy.getBySel(\"sidenav-toggle\").click();\r\n    }\r\n\r\n    cy.getBySel(\"sidenav-bankaccounts\").click();\r\n\r\n    cy.getBySel(\"bankaccount-new\").click();\r\n    cy.location(\"pathname\").should(\"eq\", \"/bankaccounts/new\");\r\n    cy.visualSnapshot(\"Display New Bank Account Form\");\r\n\r\n    cy.getBySelLike(\"bankName-input\").type(\"The Best Bank\");\r\n    cy.getBySelLike(\"routingNumber-input\").type(\"987654321\");\r\n    cy.getBySelLike(\"accountNumber-input\").type(\"123456789\");\r\n    cy.visualSnapshot(\"Fill out New Bank Account Form\");\r\n    cy.getBySelLike(\"submit\").click();\r\n\r\n    cy.wait(\"@gqlCreateBankAccountMutation\");\r\n\r\n    cy.getBySelLike(\"bankaccount-list-item\")\r\n      .should(\"have.length\", 2)\r\n      .eq(1)\r\n      .should(\"contain\", \"The Best Bank\");\r\n    cy.visualSnapshot(\"Bank Account Created\");\r\n  });\r\n\r\n  it(\"Displaying bank account form validation errors\", function () {\r\n    cy.visit(\"/bankaccounts\");\r\n    cy.getBySel(\"bankaccount-new\").click();\r\n\r\n    cy.getBySelLike(\"bankName-input\").type(\"The\");\r\n    cy.getBySelLike(\"bankName-input\").find(\"input\").clear();\r\n    cy.getBySelLike(\"bankName-input\").find(\"input\").blur();\r\n    cy.get(\"#bankaccount-bankName-input-helper-text\")\r\n      .should(\"be.visible\")\r\n      .and(\"contain\", \"Enter a bank name\");\r\n\r\n    cy.getBySelLike(\"bankName-input\").type(\"The\");\r\n    cy.getBySelLike(\"bankName-input\").find(\"input\").blur();\r\n    cy.get(\"#bankaccount-bankName-input-helper-text\")\r\n      .should(\"be.visible\")\r\n      .and(\"contain\", \"Must contain at least 5 characters\");\r\n\r\n    /** Routing number input validations **/\r\n    // Required field\r\n    cy.getBySelLike(\"routingNumber-input\").find(\"input\").focus();\r\n    cy.getBySelLike(\"routingNumber-input\").find(\"input\").blur();\r\n    cy.get(`#bankaccount-routingNumber-input-helper-text`)\r\n      .should(\"be.visible\")\r\n      .and(\"contain\", \"Enter a valid bank routing number\");\r\n\r\n    // Min 9 digit\r\n    cy.getBySelLike(\"routingNumber-input\").type(\"12345678\");\r\n    cy.getBySelLike(\"routingNumber-input\").find(\"input\").blur();\r\n\r\n    cy.get(\"#bankaccount-routingNumber-input-helper-text\")\r\n      .should(\"be.visible\")\r\n      .and(\"contain\", \"Must contain a valid routing number\");\r\n    cy.getBySelLike(\"routingNumber-input\").find(\"input\").clear();\r\n\r\n    cy.getBySelLike(\"routingNumber-input\").type(\"123456789\");\r\n    cy.getBySelLike(\"routingNumber-input\").find(\"input\").blur();\r\n    cy.get(\"#bankaccount-routingNumber-input-helper-text\").should(\"not.exist\");\r\n\r\n    /** Account number input validations **/\r\n    // Required field\r\n    cy.getBySelLike(\"accountNumber-input\").find(\"input\").focus();\r\n    cy.getBySelLike(\"accountNumber-input\").find(\"input\").blur();\r\n    cy.get(`#bankaccount-accountNumber-input-helper-text`)\r\n      .should(\"be.visible\")\r\n      .and(\"contain\", \"Enter a valid bank account number\");\r\n\r\n    // Min 9 digit\r\n    cy.getBySelLike(\"accountNumber-input\").type(\"12345678\");\r\n    cy.getBySelLike(\"accountNumber-input\").find(\"input\").blur();\r\n    cy.get(\"#bankaccount-accountNumber-input-helper-text\")\r\n      .should(\"be.visible\")\r\n      .and(\"contain\", \"Must contain at least 9 digits\");\r\n    cy.getBySelLike(\"accountNumber-input\").find(\"input\").clear();\r\n\r\n    cy.getBySelLike(\"accountNumber-input\").type(\"123456789\");\r\n    cy.getBySelLike(\"accountNumber-input\").find(\"input\").blur();\r\n    cy.get(\"#bankaccount-accountNumber-input-helper-text\").should(\"not.exist\");\r\n    cy.getBySelLike(\"accountNumber-input\").find(\"input\").clear();\r\n\r\n    // Max 12 gdigit\r\n    cy.getBySelLike(\"accountNumber-input\").type(\"123456789111\");\r\n    cy.getBySelLike(\"accountNumber-input\").find(\"input\").blur();\r\n    cy.get(\"#bankaccount-accountNumber-input-helper-text\").should(\"not.exist\");\r\n    cy.getBySelLike(\"accountNumber-input\").find(\"input\").clear();\r\n\r\n    cy.getBySelLike(\"accountNumber-input\").type(\"1234567891111\");\r\n    cy.getBySelLike(\"accountNumber-input\").find(\"input\").blur();\r\n    cy.get(\"#bankaccount-accountNumber-input-helper-text\")\r\n      .should(\"be.visible\")\r\n      .and(\"contain\", \"Must contain no more than 12 digits\");\r\n\r\n    cy.getBySel(\"bankaccount-submit\").should(\"be.disabled\");\r\n    cy.visualSnapshot(\"Bank Account Form with Errors and Submit button disabled\");\r\n  });\r\n\r\n  it(\"Soft deleting a bank account\", function () {\r\n    cy.visit(\"/bankaccounts\");\r\n    cy.getBySelLike(\"delete\").first().click();\r\n\r\n    cy.wait(\"@gqlDeleteBankAccountMutation\");\r\n    cy.getBySelLike(\"list-item\").children().contains(\"Deleted\");\r\n    cy.visualSnapshot(\"Soft Delete Bank Account\");\r\n  });\r\n\r\n  // TODO: [enhancement] the onboarding modal assertion can be removed after adding \"onboarded\" flag to user profile\r\n  it(\"Rendering empty state with onboarding modal\", function () {\r\n    cy.wait(\"@getNotifications\");\r\n    cy.intercept(\"POST\", apiGraphQL, (req) => {\r\n      const { body } = req;\r\n      if (body.hasOwnProperty(\"operationName\") && body.operationName === \"ListBankAccount\") {\r\n        req.alias = \"gqlListBankAccountQuery\";\r\n        req.continue((res) => {\r\n          res.body.data.listBankAccount = [];\r\n        });\r\n      }\r\n    });\r\n\r\n    cy.visit(\"/bankaccounts\");\r\n    cy.wait(\"@getNotifications\");\r\n    cy.wait(\"@gqlListBankAccountQuery\");\r\n\r\n    cy.getBySel(\"bankaccount-list\").should(\"not.exist\");\r\n    cy.getBySel(\"empty-list-header\").should(\"contain\", \"No Bank Accounts\");\r\n    cy.getBySel(\"user-onboarding-dialog\").should(\"be.visible\");\r\n    cy.getBySel(\"nav-top-notifications-count\").should(\"exist\");\r\n    cy.visualSnapshot(\"User Onboarding Dialog is Visible\");\r\n  });\r\n});\r\n','Feature: Bank Accounts Management\r\n\r\n  Background:\r\n    Given I am a logged-in user\r\n\r\n  @bank-accounts @creation\r\n  Scenario: Adding a new bank account\r\n    Given I am on the Bank Accounts page\r\n    When I add a new bank account with valid details\r\n    Then the account \"The Best Bank\" should be listed in my accounts\r\n\r\n  @bank-accounts @validation\r\n  Scenario Outline: Preventing invalid account details\r\n    Given I am adding a new bank account\r\n    When I attempt to save with <condition>\r\n    Then I should see the error \"<error_message>\"\r\n    And the account should not be created\r\n\r\n    Examples:\r\n      | condition                         | error_message                       |\r\n      | a missing bank name               | Enter a bank name                   |\r\n      | a short bank name                 | Must contain at least 5 characters  |\r\n      | a short routing number            | Must contain a valid routing number |\r\n      | an account number with 8 digits   | Account number must be 9-12 digits  |\r\n      | an account number with 13 digits  | Account number must be 9-12 digits  |\r\n\r\n  @bank-accounts @delete\r\n  Scenario: Deleting an existing bank account\r\n    Given I have a bank account named \"Old Bank\"\r\n    When I delete the account \"Old Bank\"\r\n    Then \"Old Bank\" should no longer appear in my active account list\r\n\r\n  @bank-accounts @onboarding\r\n  Scenario: New user onboarding prompt\r\n    Given I have no bank accounts\r\n    When I visit the Bank Accounts page\r\n    Then I should be prompted to create my first account','Cypress Real World App','High','https://github.com/cypress-io/cypress-realworld-app'),(4,'cart','import \'./commands\';\r\n\r\ndescribe(\'Shopping Cart Management\', () => {\r\n\r\n    beforeEach(() => {\r\n        // Log in before each test\r\n        cy.visit(\'https://www.saucedemo.com\');\r\n        cy.login();\r\n    });\r\n\r\n    it(\'Add a product to the shopping cart\', () => {\r\n        // Add the first product to the cart\r\n        cy.get(\'.btn_inventory\').first().click();\r\n\r\n        // Verify the cart badge shows \'1\'\r\n        cy.get(\'.shopping_cart_badge\').should(\'have.text\', \'1\');\r\n\r\n        // Go to the cart and verify the product is there\r\n        cy.get(\'.shopping_cart_link\').click();\r\n        cy.get(\'.cart_item\').should(\'have.length\', 1);\r\n        cy.get(\'.inventory_item_name\').should(\'exist\');\r\n    });\r\n\r\n    it(\'Remove a product from the shopping cart\', () => {\r\n        \r\n        // Add the first product to the cart\r\n        cy.get(\'.btn_inventory\').first().click();\r\n        \r\n        // Go to the cart\r\n        cy.get(\'.shopping_cart_link\').click();\r\n\r\n        // Verify the item is in the cart\r\n        cy.get(\'.cart_item\').should(\'have.length\', 1);\r\n        \r\n        // Remove the item\r\n        cy.get(\'.cart_button\').click();\r\n        \r\n        // Verify the cart is now empty\r\n        cy.get(\'.cart_item\').should(\'not.exist\');\r\n        cy.get(\'.shopping_cart_badge\').should(\'not.exist\');\r\n    });\r\n});','Feature: Shopping Cart Management\r\n\r\n  @cart @add\r\n  Scenario: Adding an item to the cart\r\n    Given I am browsing the product catalog\r\n    When I add \"Sauce Labs Backpack\" to my cart\r\n    Then my cart should contain 1 item\r\n\r\n  @cart @remove\r\n  Scenario: Removing an item from the cart\r\n    Given my cart contains the \"Sauce Labs Backpack\"\r\n    When I remove \"Sauce Labs Backpack\" from the cart\r\n    Then my cart should be empty','Saucedemo','Medium','https://github.com/MirceaMacarie/Saucedemo_Cypress_Test'),(5,'clear_completed_items','describe(\'Clearing Completed Todos\', function () {\r\n  beforeEach(function () {\r\n    cy.visit(\'http://localhost:8888/\');\r\n\r\n    // Setup: Create default todos by inlining the createDefaultTodos command\r\n    const TODO_ITEM_ONE = \'buy some cheese\';\r\n    const TODO_ITEM_TWO = \'feed the cat\';\r\n    const TODO_ITEM_THREE = \'book a doctors appointment\';\r\n    cy.get(\'.new-todo\')\r\n      .type(`${TODO_ITEM_ONE}{enter}`)\r\n      .type(`${TODO_ITEM_TWO}{enter}`)\r\n      .type(`${TODO_ITEM_THREE}{enter}`);\r\n  });\r\n\r\n  afterEach(() => {\r\n    cy.window().then((win) => {\r\n      win.document.activeElement.blur();\r\n    });\r\n  });\r\n\r\n  it(\'Clear all completed items from the list\', function () {\r\n    // Setup: Mark the second item as complete\r\n    cy.get(\'.todo-list li\')\r\n      .eq(1)\r\n      .find(\'.toggle\')\r\n      .check();\r\n\r\n    // Action: Click the \"Clear completed\" button\r\n    cy.get(\'.clear-completed\').click();\r\n\r\n    // Assertions\r\n    cy.get(\'.todo-list li\').should(\'have.length\', 2);\r\n    cy.get(\'.todo-list li\').eq(0).should(\'contain.text\', \'buy some cheese\');\r\n    cy.get(\'.todo-list li\').eq(1).should(\'contain.text\', \'book a doctors appointment\');\r\n  });\r\n});','Feature: Bulk Task Management\r\n\r\n  Background:\r\n    Given I have the following tasks:\r\n      | Task               | Status    |\r\n      | feed the cat       | Completed |\r\n      | buy some cheese    | Active    |\r\n\r\n  @todo @cleanup\r\n  Scenario: Removing completed tasks\r\n    When I clear the completed items\r\n    Then \"feed the cat\" should be removed from the list\r\n    And \"buy some cheese\" should remain in the list','TodoMVC','Low','https://github.com/cypress-io/cypress-example-todomvc'),(6,'complete_purchase','describe(\'Checkout Process\', () => {\r\n\r\n    beforeEach(() => {\r\n        // Log in before each test\r\n        cy.visit(\'https://www.saucedemo.com/v1/\');\r\n        cy.login();\r\n    });\r\n\r\n    it(\'Complete a purchase successfully\', () => {\r\n        // Add a product to the cart\r\n        cy.get(\'.btn_inventory\').first().click();\r\n        cy.get(\'.shopping_cart_link\').click();\r\n        \r\n        // Proceed to checkout\r\n        cy.get(\'.checkout_button\').click();\r\n        \r\n        // Fill out the user information form\r\n        cy.get(\'[data-test=\"firstName\"]\').type(\'John\');\r\n        cy.get(\'[data-test=\"lastName\"]\').type(\'Doe\');\r\n        cy.get(\'[data-test=\"postalCode\"]\').type(\'12345\');\r\n        cy.get(\'.cart_button\').click();\r\n        \r\n        // Finish the purchase\r\n        cy.get(\'.cart_button\').click();\r\n        \r\n        // Assert the completion message\r\n        cy.get(\'.complete-header\').should(\'have.text\', \'THANK YOU FOR YOUR ORDER\');\r\n        cy.get(\'.complete-text\').should(\'exist\');\r\n    });\r\n});','Feature: Checkout Process\r\n\r\n  Background:\r\n    Given I have products in my shopping cart\r\n\r\n  @checkout @happy-path\r\n  Scenario: Successful order placement\r\n    When I complete the checkout with valid shipping details\r\n    Then my order should be successfully processed\r\n    And I should see the order confirmation','Saucedemo','Medium','https://github.com/MirceaMacarie/Saucedemo_Cypress_Test'),(7,'delete_item','describe(\'Todo Item Deletion\', function () {\r\n  beforeEach(function () {\r\n    cy.visit(\'http://localhost:8888/\');\r\n\r\n    // Setup: Create default todos by inlining the createDefaultTodos command\r\n    const TODO_ITEM_ONE = \'buy some cheese\';\r\n    const TODO_ITEM_TWO = \'feed the cat\';\r\n    const TODO_ITEM_THREE = \'book a doctors appointment\';\r\n    cy.get(\'.new-todo\')\r\n      .type(`${TODO_ITEM_ONE}{enter}`)\r\n      .type(`${TODO_ITEM_TWO}{enter}`)\r\n      .type(`${TODO_ITEM_THREE}{enter}`);\r\n  });\r\n\r\n  afterEach(() => {\r\n    cy.window().then((win) => {\r\n      win.document.activeElement.blur();\r\n    });\r\n  });\r\n\r\n  it(\'Delete an item by editing it to be empty\', function () {\r\n    // Action: Edit the second item and clear its text\r\n    cy.get(\'.todo-list li\')\r\n      .eq(1)\r\n      .find(\'label\')\r\n      .dblclick();\r\n\r\n    cy.get(\'.todo-list li\')\r\n      .eq(1)\r\n      .find(\'.edit\')\r\n      .clear()\r\n      .type(\'{enter}\');\r\n\r\n    // Assertion\r\n    cy.get(\'.todo-list li\').should(\'have.length\', 2);\r\n    cy.get(\'.todo-list\').should(\'not.contain.text\', \'feed the cat\');\r\n  });\r\n});','Feature: Task Deletion Rules\r\n\r\n  Background:\r\n    Given the task \"feed the cat\" exists\r\n\r\n  @todo @edge-case\r\n  Scenario: Deleting a task via empty description\r\n    When I edit the task \"feed the cat\" so it has an empty name \r\n    Then the task should be removed from the list','TodoMVC','Low','https://github.com/cypress-io/cypress-example-todomvc'),(8,'edit_item','describe(\'Todo Item Editing\', function () {\r\n  beforeEach(function () {\r\n    cy.visit(\'http://localhost:8888/\');\r\n\r\n    // Setup: Create default todos by inlining the createDefaultTodos command\r\n    const TODO_ITEM_ONE = \'buy some cheese\';\r\n    const TODO_ITEM_TWO = \'feed the cat\';\r\n    const TODO_ITEM_THREE = \'book a doctors appointment\';\r\n    cy.get(\'.new-todo\')\r\n      .type(`${TODO_ITEM_ONE}{enter}`)\r\n      .type(`${TODO_ITEM_TWO}{enter}`)\r\n      .type(`${TODO_ITEM_THREE}{enter}`);\r\n  });\r\n\r\n  afterEach(() => {\r\n    cy.window().then((win) => {\r\n      win.document.activeElement.blur();\r\n    });\r\n  });\r\n\r\n  it(\'Edit an existing todo item\', function () {\r\n    // Action: Double-click the second item and edit it\r\n    cy.get(\'.todo-list li\')\r\n      .eq(1) // eq(1) targets the second item\r\n      .find(\'label\')\r\n      .dblclick();\r\n\r\n    cy.get(\'.todo-list li\')\r\n      .eq(1)\r\n      .find(\'.edit\')\r\n      .clear()\r\n      .type(\'buy some sausages\')\r\n      .type(\'{enter}\');\r\n\r\n    // Assertion\r\n    cy.get(\'.todo-list li\')\r\n      .eq(1)\r\n      .should(\'contain.text\', \'buy some sausages\');\r\n  });\r\n});','Feature: Task Modification\r\n\r\n  Background:\r\n    Given the task \"feed the cat\" exists\r\n\r\n  @todo @update\r\n  Scenario: Renaming a task\r\n    When I rename the task to \"buy some sausages\"\r\n    Then the task \"buy some sausages\" should be visible\r\n    And the task \"feed the cat\" should no longer be visible','TodoMVC','Low','https://github.com/cypress-io/cypress-example-todomvc'),(9,'filter_active_items','describe(\'Todo List Filtering\', function () {\r\n  beforeEach(function () {\r\n    cy.visit(\'http://localhost:8888/\');\r\n\r\n    // Setup: Create default todos by inlining the createDefaultTodos command\r\n    const TODO_ITEM_ONE = \'buy some cheese\';\r\n    const TODO_ITEM_TWO = \'feed the cat\';\r\n    const TODO_ITEM_THREE = \'book a doctors appointment\';\r\n    cy.get(\'.new-todo\')\r\n      .type(`${TODO_ITEM_ONE}{enter}`)\r\n      .type(`${TODO_ITEM_TWO}{enter}`)\r\n      .type(`${TODO_ITEM_THREE}{enter}`);\r\n  });\r\n\r\n  afterEach(() => {\r\n    cy.window().then((win) => {\r\n      win.document.activeElement.blur();\r\n    });\r\n  });\r\n\r\n  it(\'Filter the list to show only active items\', function () {\r\n    // Setup: Mark the second item as complete\r\n    cy.get(\'.todo-list li\')\r\n      .eq(1)\r\n      .find(\'.toggle\')\r\n      .check();\r\n\r\n    // Action: Click the \"Active\" filter\r\n    cy.get(\'.filters\')\r\n      .contains(\'Active\')\r\n      .click();\r\n\r\n    // Assertions\r\n    cy.get(\'.todo-list li\').should(\'have.length\', 2);\r\n    cy.get(\'.todo-list li\').eq(0).should(\'contain.text\', \'buy some cheese\');\r\n    cy.get(\'.todo-list li\').eq(1).should(\'contain.text\', \'book a doctors appointment\');\r\n  });\r\n});','Feature: Task Filtering\r\n\r\n  Background:\r\n    Given I have the following tasks:\r\n      | Task               | Status    |\r\n      | buy some cheese    | Active    |\r\n      | feed the cat       | Completed |\r\n\r\n  @todo @filtering\r\n  Scenario: Filtering for active tasks\r\n    When I view only the active tasks\r\n    Then \"buy some cheese\" should be visible\r\n    And \"feed the cat\" should be hidden','TodoMVC','Low','https://github.com/cypress-io/cypress-example-todomvc'),(10,'login','describe(\'Login Functionality\', function () {\n\n    beforeEach(() => {\n        // Visit the homepage before each test\n        cy.visit(\'https://demoblaze.com/\')\n    });\n    it(\'Successful login with valid credentials\', () => {\n        // Press login button\n        cy.get(\'#login2\').click();\n\n        // Wait for modal form to appear\n        cy.wait(3000);\n\n        // Enter credentials\n        cy.get(\'#loginusername\').type(\'demoblaze\');\n        cy.get(\'#loginpassword\').type(\'demoblaze\');\n\n        // Submit form\n        cy.get(\'#logInModal button.btn-primary\').click();\n\n        // Check that the logout button is visible to ensure user is logged in\n        cy.get(\'#logout2\', { timeout: 6000 }).should(\'be.visible\');\n\n        // Check that login button is no longer there\n        cy.get(\'#login2\').should(\'not.be.visible\');\n\n    });\n\n    it(\'Unsuccessful login with invalid credentials\', () => {\n        // Press login button\n        cy.get(\'#login2\').click();\n\n        // Wait for modal form to appear\n        cy.wait(3000);\n\n        // Enter wrong credentials\n        cy.get(\'#loginusername\').type(\'bdd-auto-cypress\');\n        cy.get(\'#loginpassword\').type(\'12345\');\n\n        // Submit form\n        cy.get(\'#logInModal button.btn-primary\').click();\n\n        // Check that the login was unsuccesful and alert is displayed\n        cy.on(\'window:alert\', (text) => {\n            expect(text).to.equal(\'User does not exist.\');\n        });\n\n        // Check that logout button is not there\n        cy.get(\'#logout2\').should(\'not.be.visible\');\n\n    });\n});','Feature: User Authentication\r\n\r\n  Background:\r\n    Given I am an unauthenticated visitor\r\n\r\n  @auth @login\r\n  Scenario: Successful login\r\n    When I log in with valid credentials\r\n    Then I should be authenticated\r\n    And I should see the user dashboard\r\n\r\n  @auth @error-handling\r\n  Scenario Outline: Login failure handling\r\n    When I attempt to log in with <condition>\r\n    Then I should see the error \"<error_message>\"\r\n    And I should remain unauthenticated\r\n\r\n    Examples:\r\n      | condition             | error_message       |\r\n      | a non-existent user   | User does not exist.|\r\n      | an incorrect password | Wrong password.     |','Demoblaze','Medium',NULL),(11,'mark_item_complete','describe(\'Todo Item Completion\', function () {\r\n  const TODO_ITEM_ONE = \'buy some cheese\';\r\n  const TODO_ITEM_TWO = \'feed the cat\';\r\n\r\n  beforeEach(function () {\r\n    cy.visit(\'http://localhost:8888/\');\r\n  });\r\n\r\n  afterEach(() => {\r\n    cy.window().then((win) => {\r\n      win.document.activeElement.blur();\r\n    });\r\n  });\r\n\r\n  it(\'Mark an item as complete\', function () {\r\n    // Setup: Create two todos by inlining the createTodo command\r\n    cy.get(\'.new-todo\').type(`${TODO_ITEM_ONE}{enter}`);\r\n    cy.get(\'.new-todo\').type(`${TODO_ITEM_TWO}{enter}`);\r\n\r\n    // Action: Mark the first item as complete\r\n    cy.get(\'.todo-list li\')\r\n      .first()\r\n      .find(\'.toggle\')\r\n      .check();\r\n\r\n    // Assertions\r\n    cy.get(\'.todo-list li\')\r\n      .first()\r\n      .should(\'have.class\', \'completed\');\r\n\r\n    cy.get(\'.todo-list li\')\r\n      .last()\r\n      .should(\'not.have.class\', \'completed\');\r\n  });\r\n});','Feature: Task Completion Status\r\n\r\n  Background:\r\n    Given I have the following active tasks:\r\n      | Task               |\r\n      | buy some cheese    |\r\n      | feed the cat       |\r\n\r\n  @todo @status\r\n  Scenario: Completing a task\r\n    When I complete the task \"buy some cheese\"\r\n    Then \"buy some cheese\" should be listed as completed\r\n    And \"feed the cat\" should remain active','TodoMVC','Low','https://github.com/cypress-io/cypress-example-todomvc'),(12,'new-transaction','import Dinero from \"dinero.js\";\r\nimport { User } from \"../../../src/models\";\r\nimport { isMobile } from \"../../support/utils\";\r\n\r\ntype NewTransactionTestCtx = {\r\n  allUsers?: User[];\r\n  user?: User;\r\n  contact?: User;\r\n};\r\n\r\ndescribe(\"New Transaction Functionality\", function () {\r\n  const ctx: NewTransactionTestCtx = {};\r\n\r\n  beforeEach(function () {\r\n    cy.task(\"db:seed\");\r\n\r\n    cy.intercept(\"GET\", \"/users*\").as(\"allUsers\");\r\n\r\n    cy.intercept(\"GET\", \"/users/search*\").as(\"usersSearch\");\r\n\r\n    cy.intercept(\"POST\", \"/transactions\").as(\"createTransaction\");\r\n\r\n    cy.intercept(\"GET\", \"/notifications\").as(\"notifications\");\r\n    cy.intercept(\"GET\", \"/transactions/public\").as(\"publicTransactions\");\r\n    cy.intercept(\"GET\", \"/transactions\").as(\"personalTransactions\");\r\n    cy.intercept(\"PATCH\", \"/transactions/*\").as(\"updateTransaction\");\r\n\r\n